dynamicLookup.nsql:
SELECT
                   @SELECT:c.currency_code:currency_code@,
                   @SELECT:c.currency_code + ' (' +
                   c.currency_symbol + ')':currency_name@
                   FROM    cmn_currencies c      WHERE   c.is_active =
                   1       AND     @FILTER@

SELECT
                    @SELECT:c.currency_code:currency_code@,
                    @SELECT:c.currency_code || ' (' ||
                    c.currency_symbol || ')':currency_name@
                    FROM    cmn_currencies c WHERE   c.is_active = 1
                    AND     @FILTER@

SELECT  @SELECT:u.id:user_id@,
                    @SELECT:r.id:resource_id@,
                    @SELECT:r.unique_name:unique_name@,
                    @SELECT:r.first_name:first_name@,
                    @SELECT:r.last_name:last_name@,
                    @SELECT:r.person_type:person_type_id@,
                    @SELECT:r.full_name:full_name@,
                    @SELECT:l.name:person_type@                FROM
                    srm_resources r,
                    cmn_sec_users u,         cmn_lookups_v l,
                    cmn_lookups_v s WHERE   u.id = r.user_id
                    AND     @FILTER@ AND     r.person_type = l.id
                    AND     l.language_code=@WHERE:PARAM:LANGUAGE@ AND
                    l.lookup_type='SRM_RESOURCE_TYPE' AND
                    u.user_status_id = s.id                     AND
                    s.language_code= @WHERE:PARAM:LANGUAGE@ AND
                    s.lookup_type='SEC_USER_STATUS' AND
                    s.lookup_code IN ('ACTIVE','LOCK')
